SELECT count(1) FROM aa01 a WHERE a.groupid IN (SELECT groupid FROM sysuser b WHERE b.account LIKE CONCAT('%',#{account},'%')) and a.ddzt like concat('%',#{ddzt},'%')
and a.inserttime >= #{inserttime}
and a.inserttime < #{finaltime}
and a.orderid LIKE CONCAT('%',#{orderid},'%')
and a.sfqr LIKE CONCAT('%',#{sfqr},'%')

SELECT count(1) FROM aa01 a WHERE a.groupid IN (SELECT groupid FROM sysuser b WHERE b.account LIKE CONCAT('%',#{account},'%')) and a.ddzt like concat('%',#{ddzt},'%') and a.jszt='01'
and a.inserttime >= #{inserttime}
and a.inserttime < #{finaltime}
and a.orderid LIKE CONCAT('%',#{orderid},'%')
and a.sfqr LIKE CONCAT('%',#{sfqr},'%')

SELECT DISTINCT a.id,a.orderid,a.groupid,b.groupname,a.price,a.amount,a.count,a.goodsid,c.goodsname,c.count gcount,a.readdress,a.account,a.username,a.phone, a.tel,a.inserttime,(SELECT aaa102 FROM aa10 WHERE aaa100='ddzt' AND aaa101=a.ddzt ) ddzt,a.expressname,a.expressid,a.jsfs,a.jszt,a.endtime,a.aab301,a.sfqr,a.ddbz,a.ckqr FROM aa01 a LEFT JOIN sysuser b ON a.groupid=b.groupid LEFT JOIN gg01 c ON a.goodsid=c.goodsid WHERE a.groupid IN (SELECT groupid FROM sysuser b WHERE b.account LIKE CONCAT('%',#{account},'%')) and a.ddzt like concat('%',#{ddzt},'%')
and a.inserttime >= #{inserttime}
and a.inserttime < #{finaltime}
and a.orderid LIKE CONCAT('%',#{orderid},'%')
and a.sfqr LIKE CONCAT('%',#{sfqr},'%')

