mapper.select:
SELECT id, username, PASSWORD, aab301, account, telphone, sex,groupname,groupid
FROM sysuser  where 1=1

    limit #{start},#{end}

mapper.insert:
insert into sysuser(
username, aab301, account, telphone, password, sex, groupname, groupid, inserttime
)values(
#{username}, #{aab301}, #{account}, #{telphone}, #{password}, #{sex}, #{groupname}, #{groupid}, #{inserttime}
)

mapper.update:
update sysuser
account =#{account}, username =#{username}, password =#{password}, sex =#{sex}, telphone =#{telphone}
where id = #{id}

mapper.select:
SELECT id, userId, roleId
FROM user_role
where userid = #{userId}

select COUNT(1) from sysuser
 where account=#{account}
   and password =#{password}

mapper.update:
update sysuser
 set password =#{newpassword}
  where account=#{account}
